import React, { useState, useEffect, useRef, useContext } from "react";

import { Modal } from "../components/Modal/Modal";

import Backdrop from "../components/Backdrop/Backdrop";
import GameList from "../components/Games/GameList/GameList";
import Spinner from "../components/Spinner/Spinner";
import AuthContext from "../context/auth-context";
import "./Games.css";

const GamesPage = () => {
  const [creating, setCreating] = useState(false);
  const [games, setGames] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedGame, setSelectedGame] = useState(null);

  const context = useContext(AuthContext);

  const titleElRef = useRef();
  const priceElRef = useRef();
  const dateElRef = useRef();
  const descriptionElRef = useRef();

  useEffect(() => {
    fetchGames();
  }, []);

  const startCreateGameHandler = () => {
    setCreating(true);
  };

  const modalConfirmHandler = () => {
    setCreating(false);
    const title = titleElRef.current.value;
    const price = +priceElRef.current.value;
    const date = dateElRef.current.value;
    const description = descriptionElRef.current.value;

    if (
      title.trim().length === 0 ||
      price <= 0 ||
      date.trim().length === 0 ||
      description.trim().length === 0
    ) {
      return;
    }

    const game = { title, price, date, description };
    console.log(game);

    const requestBody = {
      query: `
        mutation {
          createGame(gameInput: {title: "${title}", description: "${description}", price: ${price}, date: "${date}"}) {
            _id
            title
            description
            date
            price
          }
        }
      `,
    };

    const token = context.token;

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed!");
        }
        return res.json();
      })
      .then((resData) => {
        setGames((prevGames) => [
          ...prevGames,
          {
            _id: resData.data.createGame._id,
            title: resData.data.createGame.title,
            description: resData.data.createGame.description,
            date: resData.data.createGame.date,
            price: resData.data.createGame.price,
            creator: {
              _id: context.userId,
            },
          },
        ]);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const modalCancelHandler = () => {
    setCreating(false);
    setSelectedGame(null);
  };

  const fetchGames = () => {
    setIsLoading(true);
    const requestBody = {
      query: `
        query {
          games {
            _id
            title
            description
            date
            price
            creator {
              _id
              email
            }
          }
        }
      `,
    };

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed!");
        }
        return res.json();
      })
      .then((resData) => {
        const games = resData.data.games;
        setGames(games);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setIsLoading(false);
      });
  };

  const showDetailHandler = (gameId) => {
    setSelectedGame((prevGames) => prevGames.find((g) => g._id === gameId));
  };

  const bookGameHandler = () => {};

  return (
    <>
      {(creating || selectedGame) && <Backdrop />}
      {creating && (
        <Modal
          title="Add Game"
          canCancel
          canConfirm
          onCancel={modalCancelHandler}
          onConfirm={modalConfirmHandler}
          confirmText="Confirm"
        >
          <form>
            <div className="form-control">
              <label htmlFor="title">Title</label>
              <input type="text" id="title" ref={titleElRef} />
            </div>
            <div className="form-control">
              <label htmlFor="price">Price</label>
              <input type="number" id="price" ref={priceElRef} />
            </div>
            <div className="form-control">
              <label htmlFor="date">Date</label>
              <input type="datetime-local" id="date" ref={dateElRef} />
            </div>
            <div className="form-control">
              <label htmlFor="description">Description</label>
              <textarea id="description" rows="4" ref={descriptionElRef} />
            </div>
          </form>
        </Modal>
      )}
      {selectedGame && (
        <Modal
          title={selectedGame.title}
          canCancel
          canConfirm
          onCancel={modalCancelHandler}
          onConfirm={bookGameHandler}
          confirmText="Book"
        >
          <h1>{selectedGame.title}</h1>
          <h2>
            ${selectedGame.price} -{" "}
            {new Date(selectedGame.date).toLocaleDateString()}
          </h2>
          <p>{selectedGame.description}</p>
        </Modal>
      )}
      {context.token && (
        <div className="games-control">
          <p>Share your own Games!</p>
          <button className="btn" onClick={startCreateGameHandler}>
            Create Game
          </button>
        </div>
      )}
      {isLoading ? (
        <Spinner />
      ) : (
        <GameList
          games={games}
          authUserId={context.userId}
          onViewDetail={showDetailHandler}
        />
      )}
    </>
  );
};

export default GamesPage;
